function slideshow_user_events(){$('.slideshow').on('click','.slide_rail_images img',function(){var parent=get_slideshow_id($(this));var new_slide=$(this).parent().index();change_slide(new_slide,parent);});$('.slideshow').on('click','.show_overlay .slide_left, .show_overlay .slide_right',function(){var parent=get_slideshow_id($(this));var new_slide=get_slide_index($(this),parent);slide_change_request(new_slide,parent);});$('.slideshow').on('click','.slide_rail .slide_left, .slide_rail .slide_right',function(){var parent=get_slideshow_id($(this));var new_slide=get_slide_index($(this),parent);change_rail_images(new_slide,parent,'rail');});$('.slideshow').on('mouseover',function(){var node=$(this);$(document).on('keydown',function(e){var parent=get_slideshow_id(node);var json=$(parent).data('slide').json;var new_slide=$(parent).data('slide').slide_pos;switch(e.keyCode){case 37:if(new_slide-1<0)break;slide_change_request(new_slide-1,parent);break;case 39:if(new_slide+1==json.slides.length)break;slide_change_request(new_slide+1,parent);break;}});});$('.slideshow').on('mouseout',function(){$(document).off('keydown');});}
function get_slideshow_id(node){var id=node.hasClass('slideshow')?'#'+node.attr('id'):'#'+node.parents('.slideshow').attr('id');return id;}
function get_slide_index(node,parent){var slide_change=node.hasClass('slide_left')?-1:1;var id=$(parent).data('slide').slide_pos+slide_change;return id;}
function slide_change_request(new_slide,parent){change_rail_images(new_slide,parent,'img');change_slide(new_slide,parent);}
function change_slide(slide_num,parent){var json=($(parent).data('slide').json);var slide_total=json.slides.length;var img_num=slide_num+1;var prev_slide=slide_num-1;var rhs=slideshow_pos(parent);var move_right_arrows=$(parent+' .show_overlay .slide_right');var move_left_arrows=$(parent+' .show_overlay .slide_left');var slide=json.slides[slide_num];if(!slide)return;$(parent+' .current_slide_item').removeClass('current_slide_item');$(parent+' .slide_rail .current').removeClass('current');if($(parent+' .slide'+prev_slide).size()<1&&prev_slide>0)
load_slide_img(img_num-1,parent);if($(parent+' .slide'+slide_num).size()<1)
load_slide_img(img_num,parent);if($(parent+' .slide'+img_num).size()<1&&img_num<slide_total)
load_slide_img(img_num+1,parent);if(!rhs){var rail_img=$(parent+' .slide_rail_images .rail_img');rail_img.eq(slide_num).addClass('current');}
$(parent+' h3').text(slide.title);$(parent+' .slide_body').html(slide.description);$(parent+' .slide'+slide_num).addClass('current_slide_item');$(parent).data("slide",{'slide_pos':slide_num,'json':json});if(!slide.video_url){var pos_from_top=slide_img_v_alignment(slide);$(parent+' .slide'+slide_num).css('top',pos_from_top);}
if(slide.title)$(parent+' h3').show();else $(parent+' h3').hide();if(img_num==slide_total)move_right_arrows.hide();else move_right_arrows.show();if(img_num==1)move_left_arrows.hide();else move_left_arrows.show();$(parent+' .current_slide_item').show();$(parent+' .slide_count').html(slide_num+1+' of '+slide_total);if(slide.video_url)$.load_reg_media_players();}
function change_rail_images(slide,parent,change_type){var current_slide=$(parent+' .rail_img.current').index();var visible_slides=rail_size(parent);var json=($(parent).data('slide').json);var slide_total=json.slides.length;var first_visible_slide=$(parent+' .slide_rail_images img:visible').parent().eq(0).index();var last_visible_slide=first_visible_slide+visible_slides;var rail_img=$(parent+' .slide_rail_images .rail_img');var left_rail_arrow=$('.slide_rail .slide_left');var right_rail_arrow=$('.slide_rail .slide_right');if(slide>current_slide){var max_right_pos=slide_total-visible_slides-1;if(change_type=='img'&&!rail_img.eq(last_visible_slide).hasClass('current'))return;else if(change_type=='rail'&&rail_img.eq(first_visible_slide).hasClass('current'))
change_slide(current_slide+1,parent);if(first_visible_slide==max_right_pos-1)right_rail_arrow.hide();if(first_visible_slide<max_right_pos){rail_img.eq(first_visible_slide).hide();rail_img.eq(last_visible_slide+1).show();left_rail_arrow.show();}}
else if(slide<current_slide){if(change_type=='img'&&!rail_img.eq(first_visible_slide).hasClass('current'))return;else if(change_type=='rail'&&rail_img.eq(last_visible_slide).hasClass('current'))
change_slide(current_slide-1,parent);if(first_visible_slide==1)left_rail_arrow.hide();if(first_visible_slide>0){rail_img.eq(last_visible_slide).hide();rail_img.eq(first_visible_slide-1).show();right_rail_arrow.show();}}}
