// GLOBAL VARIABLES
var windowSize = '';
var windowWidth = 0;
var actualSize = 0;


// DOC 		
$(document).ready(function(){

	checkBrowserSize();
	setInterval(checkBrowserSize, 500);

	$('a.staff').bind('click tap', function () {
		if ($(this).hasClass('activeDrawer')) {
			//Reset
			$('.activeDrawer').each(function( index ){
        	    //Remove 'open' class
        	    $(this).removeClass('activeDrawer');
        	});
	
			$('.open').each(function( index ){
        	    //Remove 'open' class
        	    $(this).removeClass('open');
        	});
	
        	$('.shown').each(function ( index){
        		//Close all open
        		$(this).removeClass('shown');
        		openClose(this);
        	});	
		} else {

		//Reset
		$('.activeDrawer').each(function( index ){
            //Remove 'open' class
            $(this).removeClass('activeDrawer');
        });

		$('.open').each(function( index ){
            //Remove 'open' class
            $(this).removeClass('open');
        });

        $('.shown').each(function ( index){
        	//Close all open
        	$(this).removeClass('shown');
        	openClose(this);
        });

		$(this).addClass('activeDrawer');
		//Get ID of cell

		var cellID = $(this).attr('id');

		var cellContentID = document.getElementById(cellID);
		cellContentID = cellContentID.dataset.drawercontent;

		//Figure out what content drawer you should open
		var rowClass = $(this).attr('class');

		rowClass = splitClass(rowClass);
			
		//set drawer ID and open
		var drawerID = rowClass+"-drawer";	
	
		drawerID = document.getElementById(drawerID);
		pullContent(drawerID, cellContentID);

		$(drawerID).addClass('open');

		}
	});
	
});


//FUNCTIONS

function checkBrowserSize(){
	windowWidth = window.outerWidth;
	var contentWidth = $('body').width();
	var sizeDiff = windowWidth - contentWidth;
	actualSize = windowWidth - sizeDiff;

	if(actualSize > 1316){ newWindowSize = 'large'; }

	if(actualSize <= 1316 && actualSize > 591){ newWindowSize='medium'; }

	if(actualSize <= 591){ newWindowSize ='small'; }

	if(windowSize != newWindowSize){ 
		windowSize = newWindowSize;
		removeContentSpace();
		addContentSpace(windowSize);
	}
}

function addContentSpace(windowSize){
	var rowCount = 0;
	var drawerCount = 0;

	if (windowSize == "large") {
		$('.staff-level').each(function( index ){
		var counter = 0;

		//var items = $(this).find('a.staff').length;
		var items = $(this).find('.staff').length;

			if (items < 4) {
				//(this).find('a.staff').each(function( index ){
				$(this).find('.staff').each(function( index ){	
					$(this).addClass(rowCount+"-row");
				});		

				//$(this).find('a.staff').last().after( "<div id='"+drawerCount+"-drawer' class='content-drawer'></div>" );
				$(this).find('.staff').last().after( "<div id='"+drawerCount+"-drawer' class='content-drawer'></div>" );
				drawerCount = drawerCount + 1;
				rowCount = rowCount + 1;

			} else if ((items > 4) && (items%4)!=0){
				
				$(this).find('.staff').each(function( index ){	
						$(this).addClass(rowCount+"-row");
						counter = counter + 1;

						if (counter%4 == 0){
							$(this).after( "<div id='"+drawerCount+"-drawer' class='content-drawer'></div>" );
							drawerCount = drawerCount + 1;
							rowCount = rowCount + 1;

						} else if (items == counter) {
							$(this).after( "<div id='"+drawerCount+"-drawer' class='content-drawer'></div>" );
							drawerCount = drawerCount + 1;
							rowCount = rowCount + 1;
						}
					});		

			} else {

				if (items >= index) {
					//$(this).find('a.staff').each(function( index ){
					$(this).find('.staff').each(function( index ){
						$(this).addClass(rowCount+"-row");
						counter = counter + 1;

						if (counter%4 == 0){
							$(this).after( "<div id='"+drawerCount+"-drawer' class='content-drawer'></div>" );
							drawerCount = drawerCount + 1;
							rowCount = rowCount + 1;
						}
					});	
				} else {
					return false;
				}
			}
		});
	}

	if (windowSize == "medium") {
		$('.staff-level').each(function( index ){
		var counter = 0;

		//var items = $(this).find('a.staff').length;
		var items = $(this).find('.staff').length;

			if (items < 2) {
				$(this).find('.staff').each(function( index ){					
					$(this).addClass(rowCount+"-row");
				});		

				$(this).find('.staff').last().after( "<div id='"+drawerCount+"-drawer' class='content-drawer'></div>" );

			} else if ((items > 2) && (items%2)!=0){
				
				$(this).find('.staff').each(function( index ){	
						$(this).addClass(rowCount+"-row");
						counter = counter + 1;

						if (counter%2 == 0){
							$(this).after( "<div id='"+drawerCount+"-drawer' class='content-drawer'></div>" );
							drawerCount = drawerCount + 1;
							rowCount = rowCount + 1;

						} else if (items == counter) {
							$(this).after( "<div id='"+drawerCount+"-drawer' class='content-drawer'></div>" );
							drawerCount = drawerCount + 1;
							rowCount = rowCount + 1;
						}
					});	

			} else {
				if (items >= index) {
					//$(this).find('a.staff').each(function( index ){
					$(this).find('.staff').each(function( index ){	
						$(this).addClass(rowCount+"-row");
						counter = counter + 1;

						if (counter%2 == 0){
							$(this).after( "<div id='"+drawerCount+"-drawer' class='content-drawer'></div>" );
							drawerCount = drawerCount + 1;
							rowCount = rowCount + 1;
						}
					});	
				} else {
					return false;
				}
			}
		});
	}

	if (windowSize == "small") {
		$('.staff-level').each(function( index ){
			var counter = 0;

			//var items = $(this).find('a.staff').length;
			var items = $(this).find('.staff').length;
		
			//$(this).find('a.staff').each(function( index ){
			$(this).find('.staff').each(function( index ){
				$(this).addClass(rowCount+"-row");
				$(this).after( "<div id='"+drawerCount+"-drawer' class='content-drawer'></div>" );
				drawerCount = drawerCount + 1;
				rowCount = rowCount + 1;
			});	
		});
	}
}

function removeContentSpace(){
	$('.content-drawer').remove();	

	var cellCount = 0;

	//$('a.staff').each(function( index ){
	$('.staff').each(function( index ){	
		var classes = $(this).attr('class').split(/\s+/);
		$(this).removeClass(classes[2]);
	});	
}

function openClose(drawer){
	$(drawer).slideToggle(300);
	//$(content).slideToggle(300);
};

function pullContent(drawerID, cellContentID){
	//Get drawer needed
	var drawerIDpicked = '#'+$(drawerID).attr('id');
	var contentPicked = '#'+cellContentID+'-bio';
	
	//clone content and stick into appropriate drawer
	var contentDisplay = $(contentPicked).clone();

	$(drawerIDpicked).html(contentDisplay);

	openClose(contentDisplay);
	$(contentDisplay).addClass('shown');
}

function splitClass(classBeingSplit){
	var splitClass = classBeingSplit.split(/\s+/);
	splitClass = splitClass[2];
	splitClass = splitClass.match(/\d+/)[0];

	return splitClass;
}	

